SELECT Invoice.Total, (Customer.FirstName||" "||Customer.LastName) AS CustomerFullName, Customer.Country, (Employee.FirstName||" "||Employee.LastName) AS SaleAgentFullName FROM Invoice LEFT JOIN Customer ON Invoice.CustomerId=Customer.CustomerId JOIN Employee ON Customer.SupportRepId==Employee.EmployeeId
UNION
SELECT Invoice.Total, (Customer.FirstName||" "||Customer.LastName) AS CustomerFullName, Customer.Country, (Employee.FirstName||" "||Employee.LastName) AS SaleAgentFullName FROM Customer LEFT JOIN Invoice ON Invoice.CustomerId=Customer.CustomerId JOIN Employee ON Customer.SupportRepId==Employee.EmployeeId;